import React, { useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import './TechStack.css';

function TechStack() {
  const [hoveredTech, setHoveredTech] = useState(null);
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: false,
  });

  const techStack = [
    { name: 'Kubernetes', logo: '/logos/kubernetes.svg', color: '#326CE5', url: 'https://kubernetes.io', desc: 'Container Orchestration' },
    { name: 'Docker', logo: '/logos/dockvg', color: '#4EAA25', url: 'https://gnu.org/software/bash' },
    { name: 'Groovy', logo: '/logos/groovy.svg', color: '#4298B8', url: 'https://groovy-lang.org' },
    { name: 'Git', logo: '/logos/git.svg', color: '#F05032', url: 'https://git-scm.com' },
    { name: 'GitHub', logo: '/logos/github.svg', color: '#181717', url: 'https://github.com' },
    { name: 'Ubuntu', logo: '/logos/linux.svg', color: '#E95420', url: 'https://ubuntu.com' },
    { name: 'Prometheus', logo: '/logos/prometheus.svg', color: '#E6522C', url: 'https://prometheus.io' },
    { name: 'Grafana', logo: '/logos/grafana.svg', color: '#F46800', url: 'https://grafana.com' },
    { name: 'YAML', logo: '/logos/yaml.svg', color: '#CB171E', url: 'https://yaml.org' },
  ];

  return (
    <div className="tech-stack-container">
      <div className="tech-stack-grid">
        {techStack.map((tech, index) => (
          <motion.a
            key={tech.name}
            href={tech.url}
            target="_blank"
            rel="noopener noreferrer"
            className="tech-item"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ 
              duration: 0.6, 
              delay: index * 0.1,
              ease: "easeOut"
            }}
            whileHover={{ scale: 1.08, y: -5 }}
            whileTap={{ scale: 0.95 }}
          >
            <div className="tech-logo-wrapper" style={{ '--tech-color': tech.color }}>
              <img src={tech.logo} alt={tech.name} className="tech-logo" />
            </div>
            <span className="tech-name">{tech.name}</span>
          </motion.a>
        ))}
      </div>

      <div className="devops-infinity">
        <svg viewBox="0 0 200 100" className="infinity-svg">
          <path
            d="M 20,50 C 20,20 40,20 50,50 C 60,80 80,80 100,50 C 120,20 140,20 150,50 C 160,80 180,80 180,50"
            fill="none"
            stroke="var(--accent-primary)"
            strokeWidth="3"
            className="infinity-path"
          />
        </svg>
        <div className="devops-text">
          <span className="dev">Dev</span>
          <span className="ops">Ops</span>
        </div>
      </div>
    </div>
  );
}

export default TechStack;
