import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import './TechShowcase.css';

function TechShowcase() {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const techStack = [
    { name: 'Kubernetes', logo: '/logos/kubernetes.svg' },
    { name: 'Docker', logo: '/logos/docker.svg' },
    { name: 'Jenkins', logo: '/logos/jenkins.svg' },
    { name: 'ArgoCD', logo: '/logos/argocd.svg' },
    { name: 'Terraform', logo: '/logos/terraform.svg' },
    { name: 'Ansible', logo: '/logos/ansible.svg' },
    { name: 'AWS', logo: '/logos/aws.svg' },
    { name: 'Python', logo: '/logos/python.svg' },
    { name: 'Bash', logo: '/logos/bash.svg' },
    { name: 'Git', logo: '/logos/git.svg' },
    { name: 'GitHub', logo: '/logos/github.svg' },
    { name: 'Ubuntu', logo: '/logos/linux.svg' },
  ];

  return (
    <div className="tech-showcase" ref={ref}>
      <div className="container">
        <motion.div
          className="tech-header"
          initial={{ opacity: 0, y: 20 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
        >
          <h2>Technologies</h2>
          <p className="tech-description">Tools and technologies I work with daily</p>
        </motion.div>

        <div className="tech-grid">
          {techStack.map((tech, index) => (
            <motion.div
              key={tech.name}
              className="tech-card"
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.4, delay: index * 0.05 }}
              whileHover={{ y: -4 }}
            >
              <span className="tech-label">{tech.name}</span>
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default TechShowcase;
