import React, { useState, useEffect } from 'react';
import { useTheme } from '../context/ThemeContext';
import './Navbar.css';

function Navbar() {
  const [scrolled, setScrolled] = useState(false);
  const { isDark, toggleTheme } = useTheme();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-container">
        <div className="navbar-logo" onClick={() => scrollToSection('hero')}>
          <div className="logo-icon">
            <svg width="64" height="32" viewBox="0 0 80 40" fill="none" xmlns="http://www.w3.org/2000/svg">
              {/* Left Loop */}
              <path d="M 15,20 A 10,10 0 1,1 15,19.9" stroke="var(--accent-primary)" strokeWidth="6" fill="none" className="infinity-path"/>
              {/* Right Loop */}
              <path d="M 45,20 A 10,10 0 1,1 45,19.9" stroke="var(--accent-primary)" strokeWidth="6" fill="none" className="infinity-path"/>
              {/* Center */}
              <path d="M 25,20 L 35,20" stroke="var(--accent-primary)" strokeWidth="6" fill="none" className="infinity-path"/>
              {/* DEV */}
              <circle cx="15" cy="20" r="7" fill="var(--bg-primary)"/>
              <text x="15" y="23" fontSize="6" fontWeight="900" fill="var(--text-primary)" textAnchor="middle">D</text>
              {/* OPS */}
              <circle cx="45" cy="20" r="7" fill="var(--bg-primary)"/>
              <text x="45" y="23" fontSize="6" fontWeight="900" fill="var(--text-primary)" textAnchor="middle">O</text>
            </svg>
          </div>
          <div className="logo-text-wrapper">
            <span className="logo-text">Nayan Zagade</span>
            <span className="logo-subtitle">Cloud & DevOps Engineer</span>
          </div>
        </div>
        <ul className="navbar-menu">
          <li onClick={() => scrollToSection('hero')}>Home</li>
          <li onClick={() => scrollToSection('skills')}>Skills</li>
          <li onClick={() => scrollToSection('experience')}>Experience</li>
          <li onClick={() => scrollToSection('projects')}>Projects</li>
          <li onClick={() => scrollToSection('contact')}>Contact</li>
          <li>
            <button className="theme-toggle" onClick={toggleTheme} aria-label="Toggle theme">
              {isDark ? '○' : '●'}
            </button>
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
