import React, { useRef, useMemo } from 'react';
import { useFrame } from '@react-three/fiber';
import { Line, Text, Float, Sphere, Box, Torus } from '@react-three/drei';
import * as THREE from 'three';

// Simple DevOps Icon
function SimpleIcon({ position, color, shape = 'sphere' }) {
  const meshRef = useRef();

  useFrame((state) => {
    if (meshRef.current) {
      meshRef.current.rotation.x += 0.01;
      meshRef.current.rotation.y += 0.01;
    }
  });

  return (
    <Float speed={2} rotationIntensity={0.5} floatIntensity={2}>
      <group position={position}>
        {shape === 'sphere' && (
          <Sphere ref={meshRef} args={[0.3, 16, 16]}>
            <meshStandardMaterial color={color} wireframe />
          </Sphere>
        )}
        {shape === 'box' && (
          <Box ref={meshRef} args={[0.5, 0.5, 0.5]}>
            <meshStandardMaterial color={color} wireframe />
          </Box>
        )}
        {shape === 'torus' && (
          <Torus ref={meshRef} args={[0.3, 0.1, 16, 32]}>
            <meshStandardMaterial color={color} wireframe />
          </Torus>
        )}
      </group>
    </Float>
  );
}



// Particle System
function ParticleField() {
  const particlesRef = useRef();
  const particleCount = 200;

  const particles = useMemo(() => {
    const positions = new Float32Array(particleCount * 3);
    const colors = new Float32Array(particleCount * 3);

    for (let i = 0; i < particleCount; i++) {
      positions[i * 3] = (Math.random() - 0.5) * 30;
      positions[i * 3 + 1] = (Math.random() - 0.5) * 30;
      positions[i * 3 + 2] = (Math.random() - 0.5) * 20 - 5;

      const color = new THREE.Color();
      color.setHSL(Math.random() * 0.6 + 0.5, 0.8, 0.6);
      colors[i * 3] = color.r;
      colors[i * 3 + 1] = color.g;
      colors[i * 3 + 2] = color.b;
    }

    return { positions, colors };
  }, []);

  useFrame((state) => {
    if (particlesRef.current) {
      particlesRef.current.rotation.y += 0.0003;
      const positions = particlesRef.current.geometry.attributes.position.array;

      for (let i = 0; i < particleCount; i++) {
        positions[i * 3 + 1] += Math.sin(state.clock.elapsedTime + i) * 0.001;
      }

      particlesRef.current.geometry.attributes.position.needsUpdate = true;
    }
  });

  return (
    <points ref={particlesRef}>
      <bufferGeometry>
        <bufferAttribute
          attach="attributes-position"
          count={particleCount}
          array={particles.positions}
          itemSize={3}
        />
        <bufferAttribute
          attach="attributes-color"
          count={particleCount}
          array={particles.colors}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial size={0.08} vertexColors transparent opacity={0.6} sizeAttenuation />
    </points>
  );
}

// Connecting Lines between icons
function ConnectionLines() {
  const linesRef = useRef();

  useFrame((state) => {
    if (linesRef.current) {
      linesRef.current.material.opacity = 0.3 + Math.sin(state.clock.elapsedTime) * 0.1;
    }
  });

  const connections = [
    [[-3, 2, -3], [3, 2, -3]],
    [[3, 2, -3], [3, -2, -3]],
    [[3, -2, -3], [-3, -2, -3]],
    [[-3, -2, -3], [-3, 2, -3]],
  ];

  return (
    <group ref={linesRef}>
      {connections.map((points, i) => (
        <Line
          key={i}
          points={points}
          color="#00d4ff"
          lineWidth={1}
          transparent
          opacity={0.3}
          dashed
          dashScale={50}
          dashSize={0.1}
          gapSize={0.05}
        />
      ))}
    </group>
  );
}

function Scene3D() {
  return (
    <>
      {/* Lighting */}
      <ambientLight intensity={0.3} />
      <pointLight position={[10, 10, 10]} intensity={1} color="#00d4ff" />
      <pointLight position={[-10, -10, -10]} intensity={0.5} color="#ff6b6b" />
      <spotLight position={[0, 15, 5]} angle={0.5} penumbra={1} intensity={0.8} color="#4ecdc4" />

      {/* DevOps Infinity Loop */}
      <InfinityLoop />

      {/* Tech Stack Icons arranged in a circle */}
      <TechIcon position={[-3, 2, -3]} label="Kubernetes" color="#326CE5" icon="â˜¸" />
      <TechIcon position={[0, 3, -3]} label="Docker" color="#2496ED" icon="ðŸ³" />
      <TechIcon position={[3, 2, -3]} label="Jenkins" color="#D24939" icon="âš™" />
      <TechIcon position={[3.5, 0, -3]} label="Git" color="#F05032" icon="âŽ‡" />
      <TechIcon position={[3, -2, -3]} label="GitHub" color="#6e5494" icon="âš¡" />
      <TechIcon position={[0, -3, -3]} label="Terraform" color="#7B42BC" icon="â–²" />
      <TechIcon position={[-3, -2, -3]} label="Linux" color="#FCC624" icon="ðŸ§" />
      <TechIcon position={[-3.5, 0, -3]} label="ArgoCD" color="#EF7B4D" icon="ðŸ”„" />

      {/* Connection Lines */}
      <ConnectionLines />

      {/* Particle Field */}
      <ParticleField />

      {/* Rotating Grid */}
      <mesh rotation={[Math.PI / 2, 0, 0]} position={[0, -4, -5]}>
        <planeGeometry args={[20, 20, 20, 20]} />
        <meshBasicMaterial color="#00d4ff" wireframe transparent opacity={0.1} />
      </mesh>
    </>
  );
}

export default Scene3D;
