import React from 'react';
import { motion } from 'framer-motion';
import { TypeAnimation } from 'react-type-animation';
import './Hero.css';

function Hero() {
  return (
    <div className="hero">
      <div className="container">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, ease: "easeOut" }}
        >
          <motion.h1 
            className="hero-title"
            initial={{ scale: 0.5, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Nayan Zagade
          </motion.h1>

          <h2 className="hero-subtitle">
            <TypeAnimation
              sequence={[
                'Cloud Engineer',
                2000,
                'DevOps Engineer',
                2000,
                'Infrastructure Automation Expert',
                2000,
                'CI/CD Specialist',
                2000,
              ]}
              wrapper="span"
              speed={50}
              repeat={Infinity}
            />
          </h2>

          <motion.p 
            className="hero-description"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, delay: 0.5 }}
          >
            Entry-Level Cloud & DevOps Engineer with hands-on experience in AWS, Kubernetes, Docker, Jenkins,
            and Terraform. Skilled in building CI/CD pipelines, infrastructure as code (IaC), container
            orchestration, and cost-optimized cloud deployments.
          </motion.p>

          <motion.div 
            className="hero-contact"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            <motion.a 
              href="mailto:nayanzagade7@gmail.com"
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
            >
              ðŸ“§ nayanzagade7@gmail.com
            </motion.a>
            <motion.a 
              href="tel:9960936078"
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
            >
              ðŸ“ž 9960936078
            </motion.a>
            <motion.a 
              href="https://linkedin.com/in/nayan-zagade-9152b1271" 
              target="_blank" 
              rel="noopener noreferrer"
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
            >
              ðŸ’¼ LinkedIn
            </motion.a>
          </motion.div>
        </motion.div>
      </div>
    </div>
  );
}

export default Hero;
