import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './InfinityLogo.css';

function InfinityLogo({ size = 120, animated = true }) {
  const [isHovered, setIsHovered] = useState(false);

  const infinityPath = "M 20,60 C 20,30 35,20 50,35 C 65,50 75,50 90,35 C 105,20 120,30 120,60 C 120,90 105,100 90,85 C 75,70 65,70 50,85 C 35,100 20,90 20,60 Z";

  const pathVariants = {
    hidden: {
      pathLength: 0,
      opacity: 0
    },
    visible: {
      pathLength: 1,
      opacity: 1,
      transition: {
        duration: 2,
        ease: "easeInOut"
      }
    },
    hover: {
      scale: 1.05,
      transition: {
        duration: 0.3
      }
    }
  };

  const dotVariants = {
    hidden: { scale: 0, opacity: 0 },
    visible: (i) => ({
      scale: 1,
      opacity: 1,
      transition: {
        delay: 1.5 + i * 0.2,
        duration: 0.5,
        ease: "backOut"
      }
    }),
    pulse: {
      scale: [1, 1.2, 1],
      transition: {
        duration: 2,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  return (
    <motion.div 
      className="infinity-logo-container"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      whileHover="hover"
    >
      <svg 
        width={size} 
        height={size} 
        viewBox="0 0 140 120" 
        fill="none" 
        xmlns="http://www.w3.org/2000/svg"
      >
        <motion.path
          d={infinityPath}
          stroke="var(--accent-primary)"
          strokeWidth="3"
          fill="none"
          variants={animated ? pathVariants : {}}
          initial="hidden"
          animate="visible"
        />

        <motion.circle
          cx="35"
          cy="60"
          r="4"
          fill="var(--accent-primary)"
          custom={0}
          variants={animated ? dotVariants : {}}
          initial="hidden"
          animate={isHovered ? "pulse" : "visible"}
        />

        <motion.circle
          cx="105"
          cy="60"
          r="4"
          fill="var(--accent-primary)"
          custom={1}
          variants={animated ? dotVariants : {}}
          initial="hidden"
          animate={isHovered ? "pulse" : "visible"}
        />

        <motion.text
          x="70"
          y="85"
          fontSize="14"
          fill="var(--text-secondary)"
          textAnchor="middle"
          fontWeight="600"
          letterSpacing="1"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 2, duration: 0.5 }}
        >
          DevOps
        </motion.text>
      </svg>
    </motion.div>
  );
}

export default InfinityLogo;
