import React from 'react';
import { motion } from 'framer-motion';
import './TechStack.css';

function TechStack() {
  const techStack = [
    { name: 'Kubernetes', logo: '/logos/kubernetes.svg', color: '#326CE5', url: 'https://kubernetes.io' },
    { name: 'Docker', logo: '/logos/docker.svg', color: '#2496ED', url: 'https://docker.com' },
    { name: 'Jenkins', logo: '/logos/jenkins.svg', color: '#D24939', url: 'https://jenkins.io' },
    { name: 'Git', logo: '/logos/git.svg', color: '#F05032', url: 'https://git-scm.com' },
    { name: 'GitHub', logo: '/logos/github.svg', color: '#181717', url: 'https://github.com' },
    { name: 'Terraform', logo: '/logos/terraform.svg', color: '#7B42BC', url: 'https://terraform.io' },
    { name: 'Linux', logo: '/logos/linux.svg', color: '#FCC624', url: 'https://linux.org' },
    { name: 'ArgoCD', logo: '/logos/argocd.svg', color: '#EF7B4D', url: 'https://argo-cd.readthedocs.io' },
  ];

  return (
    <div className="tech-stack-container">
      <div className="tech-stack-grid">
        {techStack.map((tech, index) => (
          <motion.div
            key={tech.name}
            className="tech-item"
            initial={{ opacity: 0, scale: 0.5 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ 
              duration: 0.5, 
              delay: index * 0.1,
              repeat: Infinity,
              repeatType: 'reverse',
              repeatDelay: 5
            }}
            whileHover={{ scale: 1.1, rotate: 5 }}
          >
            <div className="tech-logo-wrapper" style={{ '--tech-color': tech.color }}>
              <img src={tech.logo} alt={tech.name} className="tech-logo" />
            </div>
            <span className="tech-name">{tech.name}</span>
          </motion.div>
        ))}
      </div>

      <div className="devops-infinity">
        <svg viewBox="0 0 200 100" className="infinity-svg">
          <path
            d="M 20,50 C 20,20 40,20 50,50 C 60,80 80,80 100,50 C 120,20 140,20 150,50 C 160,80 180,80 180,50"
            fill="none"
            stroke="var(--accent-primary)"
            strokeWidth="3"
            className="infinity-path"
          />
        </svg>
        <div className="devops-text">
          <span className="dev">Dev</span>
          <span className="ops">Ops</span>
        </div>
      </div>
    </div>
  );
}

export default TechStack;
